import React from "react";

const Leaderboard = ({ users }) => (
  <div className="leaderboard-container">
    <h2 className="text-2xl font-bold text-gray-800 text-center mb-3 sticky">
      Leaderboard
    </h2>
    {/* Scrollable table container */}
    <div>
      <div className="max-h-80 overflow-y-auto border">
        <table className="table-auto w-full border-collapse">
          <thead>
            <tr className="bg-blue-500 text-white sticky top-0 z-10">
              <th className="px-4 py-2 w-1/6">Rank</th>
              <th className="px-4 py-2 w-2/6">Name</th>
              <th className="px-4 py-2 w-2/6">Total Points</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr
                key={user._id}
                className={index % 2 === 0 ? "bg-gray-100" : "bg-white"}
              >
                <td className="border px-4 py-2 text-center">{index + 1}</td>
                <td className="border px-4 py-2 text-center">{user.name}</td>
                <td className="border px-4 py-2 text-center">
                  {user.totalPoints}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  </div>
);

export default Leaderboard;
