import React, { useState, useEffect } from "react";
import api from "../api/api";
import UserDropdown from "../components/UserDropdown";
import Leaderboard from "../components/Leaderboard";
import AddUserForm from "../components/AddUserForm";

const HomePage = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [history, setHistory] = useState([]);
  const [lastClaimed, setLastClaimed] = useState(null);

  useEffect(() => {
    const interval = setInterval(() => {
      const fetchUsers = async () => {
        const { data } = await api.get("/");
        setUsers(data);
        setFilteredUsers(data); // Initialize filtered users
        const { data: historyData } = await api.get("/history");
        setHistory(historyData);
      };
      fetchUsers();
    }, 5000); // Poll every 5 seconds

    return () => clearInterval(interval);
  }, []);

  const handleSearch = (e) => {
    const value = e.target.value.toLowerCase();
    setSearchTerm(value);

    // Filter users based on the search term
    const filtered = users.filter((user) =>
      user.name.toLowerCase().includes(value)
    );
    setFilteredUsers(filtered);
  };

  const handleClaimPoints = async () => {
    if (!selectedUser) return alert("Please select a user");

    const { data } = await api.post("/claim", { userId: selectedUser });
    alert(`${data.user.name} earned ${data.points} points`);
    setLastClaimed({ name: data.user.name, points: data.points });
    setUsers((prev) =>
      prev.map((u) => (u._id === data.user._id ? data.user : u))
    );

    // Refresh history
    const { data: historyData } = await api.get("/history");
    setHistory(historyData);
  };

  const addUser = async (name) => {
    const { data } = await api.post("/", { name });
    setUsers((prev) => [...prev, data]);
    setFilteredUsers((prev) => [...prev, data]); // Update filtered users
  };

  return (
    <div className="bg-gray-50 min-h-screen p-6">
      <h1 className="text-3xl font-bold text-center text-blue-600 mb-8">
        User Points System
      </h1>

      {/* Search and Dropdown */}
      <div className="max-w-lg mx-auto bg-white p-6 shadow-lg rounded-lg mb-8">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearch}
          placeholder="Search for a user..."
          className="w-full border border-gray-300 rounded-lg px-4 py-2 mb-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <div className="max-h-48 overflow-y-auto">
          <UserDropdown
            users={filteredUsers}
            selectedUser={selectedUser}
            setSelectedUser={setSelectedUser}
          />
        </div>
        <button
          onClick={handleClaimPoints}
          className="mt-4 bg-blue-500 text-white font-medium px-6 py-2 rounded-lg hover:bg-blue-600 transition w-full"
        >
          Claim Points
        </button>
        {lastClaimed && (
          <p className="mt-4 text-green-600 font-medium">
            {lastClaimed.name} earned {lastClaimed.points} points in the last
            claim.
          </p>
        )}
      </div>

      {/* Add User Form */}
      <div className="max-w-lg mx-auto mb-8">
        <AddUserForm addUser={addUser} />
      </div>

      {/* Leaderboard */}
      <div className="max-w-3xl mx-auto mb-8 bg-white p-6 shadow-lg rounded-lg">
        <Leaderboard users={users} />
      </div>

      {/* Claim History */}
      <div className="max-w-3xl mx-auto bg-white p-6 shadow-lg rounded-lg">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">
          Claim History
        </h2>
        {history.length > 0 ? (
          <ul className="space-y-2 max-h-48 overflow-y-auto">
            {history.map((item) => (
              <li
                key={item._id}
                className="flex justify-between items-center bg-gray-100 px-4 py-2 rounded-lg"
              >
                <span className="font-medium text-gray-800">
                  {item.userId.name}
                </span>
                <span className="text-gray-600">{item.points} points</span>
                <span className="text-sm text-gray-500">
                  {new Date(item.claimedAt).toLocaleString()}
                </span>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No history available.</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
