import React from "react";

const Leaderboard = ({ users }) => {
  // Split the users into top 10 and remaining
  const topUsers = users.slice(0, 10); // Top 10 users
  const remainingUsers = users.slice(10); // Remaining users

  return (
    <div className="bg-white px-2 shadow-lg rounded-lg w-full max-w-3xl mx-auto">
      <h2 className="text-2xl font-semibold text-gray-800 text-center mb-2">
        Leaderboard
      </h2>

      {/* Table for Top 10 Users */}
      <table className="table-auto w-full border-collapse">
        <thead>
          <tr className="bg-blue-500 text-white">
            <th className="px-4 py-2">Rank</th>
            <th className="px-4 py-2">Name</th>
            <th className="px-4 py-2">Total Points</th>
          </tr>
        </thead>
        <tbody>
          {topUsers.map((user, index) => (
            <tr
              key={user._id}
              className={index % 2 === 0 ? "bg-gray-100" : "bg-white"}
            >
              <td className="border px-4 py-2 text-center">{index + 1}</td>
              <td className="border px-4 py-2 text-center">{user.name}</td>
              <td className="border px-4 py-2 text-center">
                {user.totalPoints}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Scrollable Table for Remaining Users */}
      {remainingUsers.length > 0 && (
        <div className="max-h-80 overflow-y-auto mt-4">
          <table className="table-auto w-full border-collapse">
            <thead>
              <tr className="bg-blue-500 text-white sticky top-0">
                <th className="px-4 py-2">Rank</th>
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Total Points</th>
              </tr>
            </thead>
            <tbody>
              {remainingUsers.map((user, index) => (
                <tr
                  key={user._id}
                  className={(index + 10) % 2 === 0 ? "bg-gray-100" : "bg-white"}
                >
                  <td className="border px-4 py-2 text-center">
                    {index + 11} {/* Ranks start after the top 10 */}
                  </td>
                  <td className="border px-4 py-2 text-center">{user.name}</td>
                  <td className="border px-4 py-2 text-center">
                    {user.totalPoints}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Leaderboard;
